import { useState } from "react"
import ToDo from "./components/ToDo"
import ToDoForm from "./components/ToDoForm"
// import CompletedTasks from "./components/CompletedTasks"
import './styles/App.css'

function App() {
  const [todos, setTodos] = useState([])
  const addTask = (userInput) => {
    if (userInput) {
      const newItem = {
        id: Date.now(),
        task: userInput,
        complete: false,
      }
      setTodos([...todos, newItem])
    }
  }
  const removeTask = (id) => {
    setTodos([...todos.filter((todo) => todo.id !== id)])
    console.log(id)
  }
  const toggleTask = (id) => {
    setTodos([
      ...todos.map((todo) =>
        todo.id === id ? { ...todo, complete: !todo.complete } : { ...todo }
      ),
    ])
    console.log(id)
  }
  return (
    <div className="App">
      <header className="header">
        <h1 className="heading">Список дел {todos.length}</h1>
      </header>
      <ToDoForm addTask={addTask} />
      <div className="tasks">
      {todos.map((todo) => {
        if (!todo.complete)
        return (
          <div>
          <h3>Задачи в процессе выполнения</h3>
          <ToDo
            todo={todo}
            key={todo.id}
            removeTask={removeTask}
            toggleTask={toggleTask}
          />
          </div>
        )
      })}
      {todos.map((todo) => {
        if (todo.complete)
        return (
          <div>
          <h3>Выполненные задачи</h3>
          <ToDo
            todo={todo}
            key={todo.id}
            removeTask={removeTask}
            toggleTask={toggleTask}
          />
          </div>

        )
      })}
      </div>
    </div>
  )
}

export default App
