function ToDo ({ todo, removeTask, handleToggle}) {
    return (
        <div key={todo.id} className="item-todo">
            <div className="{todo.complete ? 'item-text active-item' : 'item-text'}"
            onClick={toggleTask(todo.id)}
            >
                { todo.task}
            </div>
            <button className="item-delete" onClick={()=> {removeTask(todo.id)}}>
                Удалить
            </button>

        </div>    
    )
}
export default ToDo
