function ToDo ({ todo, removeTask, toggleTask}) {
    const list = todo.filter((todo) => todo.complete === true)
    return (
        <div key={list.id} className="item-list">
            <div className={list.complete ? 'item-text active-item' : 'item-text'}
            >
                { list.task}
            </div>
            <button className="item-toggle" onClick={()=> {toggleTask(list.id)}}>
                Выполнить
            </button>
            <button className="item-delete" onClick={()=> {removeTask(list.id)}}>
                Удалить
            </button>

        </div>    
    )
}
export default ToDo
